                const sectionString = `${section}`
                const sectionTitle = `## ${sectionString}\n${emoji} **${sectionString.toUpperCase()}**`;
                fileContent += `${sectionTitle}\n${content}\n`;
                sectionLinks.push(`- [${sectionString}](#${sectionString})`);
            }
        }

